mapper.update:
update
article_user_id = #{articleUserId,jdbcType=INTEGER}, article_title = #{articleTitle,jdbcType=VARCHAR}, article_view_count = #{articleViewCount,jdbcType=INTEGER}, article_comment_count = #{articleCommentCount,jdbcType=INTEGER}, article_like_count = #{articleLikeCount,jdbcType=INTEGER}, article_create_time = #{articleCreateTime,jdbcType=TIMESTAMP}, article_update_time = #{articleUpdateTime,jdbcType=TIMESTAMP}, article_is_comment = #{articleIsComment,jdbcType=INTEGER}, article_status = #{articleStatus,jdbcType=INTEGER}, article_order = #{articleOrder,jdbcType=INTEGER}, article_content = #{articleContent,jdbcType=LONGVARCHAR}, article_summary = #{articleSummary,jdbcType=VARCHAR},
where article_id = #{articleId,jdbcType=INTEGER}

mapper.select:
SELECT

FROM



        article_status=#{status}


ORDER BY article_status ASC, article_order DESC, article_id DESC
limit #{pageIndex},#{pageSize}

SELECT
FROM
WHERE article_status = 1 ORDER BY article_view_count DESC,article_order DESC, article_id DESC limit #{limit}

SELECT
FROM
WHERE article_status = 1 ORDER BY RAND() limit #{limit}

SELECT
FROM
WHERE article_status = 1 ORDER BY article_comment_count DESC,article_order DESC, article_id DESC limit #{limit}

SELECT article.article_id, article.article_user_id, article.article_title, article.article_view_count, article.article_comment_count, article.article_like_count, article.article_create_time, article.article_update_time, article.article_is_comment, article.article_status, article.article_order, article.article_summary FROM article, article_category_ref
article.article_status = 1 AND article.article_id = article_category_ref.article_id AND article_category_ref.category_id
IN
#{id}
LIMIT #{limit}
